        $this->addSql('CREATE TABLE pointsmall_items_attribute_values (attribute_value_id BIGINT AUTO_INCREMENT NOT NULL COMMENT \'商品属性值id\', attribute_id BIGINT NOT NULL COMMENT \'商品属性ID\', company_id BIGINT NOT NULL COMMENT \'公司ID\', attribuattribute_valuete_name VARCHAR(255) NOT NULL COMMENT \'商品属性值\', sort VARCHAR(15) NOT NULL COMMENT \'商品属性排序，越大越在前\', image_url LONGTEXT DEFAULT NULL COMMENT \'图片\', created INT NOT NULL, updated INT DEFAULT NULL, INDEX ix_company_id (company_id), INDEX ix_attribute_id (attribute_id), INDEX ix_attribuattribute_valuete_name (attribuattribute_valuete_name), PRIMARY KEY(attribute_value_id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB COMMENT = \'积分商品属性值表\' ');
